mapper.select:
		SELECT * FROM (
		    SELECT ROWNUM rnum, tb.* FROM (
			    SELECT S.USERSCODE, N.NOTICECODE, SUBJECT, CONTENT, TO_CHAR(CREATED,'YYYY-MM-DD') CREATED, S.NAME, ISNOTICE 
                FROM STAFF S
                JOIN staffNotice N ON S.USERSCODE = N.USERSCODE
                    
                         
			                  
		                 
	                
                WHERE ISNOTICE = 1
                ORDER BY NOTICECODE DESC
	
	        ) tb WHERE ROWNUM <= #{end}
	    ) WHERE rnum >= #{start}

		SELECT * FROM (
		    SELECT ROWNUM rnum, tb.* FROM (
			    SELECT S.USERSCODE, N.NOTICECODE, SUBJECT, CONTENT, TO_CHAR(CREATED,'YYYY-MM-DD') CREATED, S.NAME, ISNOTICE 
                FROM STAFF S
                JOIN staffNotice N ON S.USERSCODE = N.USERSCODE
                    
                         
			                  
		                 
	                
	            ORDER BY NOTICECODE DESC
	
	        ) tb WHERE ROWNUM <= #{end}
	    ) WHERE rnum >= #{start}
